    private void formWindowActivated(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowActivated
        int cantidadNumeros = 5; // Cantidad de números aleatorios únicos a generar
        int maximo = 10; // Este es el número máximo (no se incluye)
        Random random = new Random();
        Set<Integer> numerosUnicos = new HashSet<>();

        while (numerosUnicos.size() < cantidadNumeros) {
            int numeroAleatorio = random.nextInt(maximo);
            numerosUnicos.add(numeroAleatorio);
        }
    }//GEN-LAST:event_formWindowActivated

