    private void bregistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bregistrarActionPerformed

        if (tnombre.getText().isEmpty() || tedad.getText().isEmpty() || tcorreo.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Todos los campos son obligatorios", "Error", JOptionPane.ERROR_MESSAGE);
            return;

        }
        int edad = Integer.parseInt(tedad.getText());
        if (edad < 18 || edad > 50) {
            JOptionPane.showMessageDialog(null, "La edad debe estar entre 18 y 50 años", "Alerta", JOptionPane.WARNING_MESSAGE); // Mostrar una alerta en caso de que la edad no esté dentro del rango permitido
        }
        
        clsencuesta.setEdad(Integer.parseInt(tedad.getText()));
        clsencuesta.setCorreoElectronico(tcorreo.getText());
        tcorreo.setText("");
        tedad.setText("");
        tnombre.setText("");
        tnencuesta.setText("");
        
          
    }//GEN-LAST:event_bregistrarActionPerformed

